function showclans()
{
    global $con;
    $query = $con->query("SELECT * FROM clans");
    if ($query->num_rows <= 0) {
        echo "No clans found.";
        return;
    }
    while ($result = $query->fetch_object()) {
        echo <<<_HTML
 <div class="col-md-4">
        <div class="thumbnail" style="height:270px !important">
           <img src="data:image/jpeg;base64," >
           <div class="caption">
                <h4>$result->clan_shortname</h4>
                <p>$result->clan_name</p>
                
                <p><a href="#" class="btn btn-default btn-block" role="button">View more</a></p>
            </div>
        </div>
    </div>
_HTML;
    }
}
?>
<div id="content">
    <div class="text-center registerclanbtncontainer" style="margin-bottom: 20px;">
        <a class="btn btn-info btn-lg" href="./?registerclan">Register a new Clan</a>
    </div>
    <?=showclans()?>

</div>